const avg = a => a.length ? a.reduce((s, v) => s + v, 0) / a.length : null;
const fmtMC = v => v == null ? "" : `$${(v / 1000).toFixed(1)}T`;

function buildSSAgg(items) {
  const map = {};
  items.forEach(s => {
    const k = s.fullSub;
    if (!map[k]) map[k] = { tickers: [], deltaEbits: [], marginChgs: [], pePercents: [], pePremVals: [], returns: [], drawUpPres: [], drawUpPosts: [], totalMC: 0, sector: s.sector, subSector: s.subSector };
    const m = map[k];
    m.tickers.push(s);
    if (s.marketCap != null) m.totalMC += s.marketCap;
    if (s.deltaEbit != null) m.deltaEbits.push(s.deltaEbit);
    if (s.marginChg != null) m.marginChgs.push(s.marginChg);
    if (s.pePercent10Y != null) { m.pePercents.push(s.pePercent10Y); m.pePremVals.push(s.pePercent10Y - 100); }
    if (s.returnSince != null) m.returns.push(s.returnSince);
    if (s.drawUpPre != null) m.drawUpPres.push(s.drawUpPre);
    if (s.drawUpPost != null) m.drawUpPosts.push(s.drawUpPost);
  });
  const result = {};
  Object.entries(map).forEach(([k, m]) => {
    const rs = riskScore(m.sector, m.subSector);
    result[k] = {
      name: k, count: m.tickers.length, totalMC: Math.round(m.totalMC),
      bubbleMC: m.totalMC > 0 ? Math.sqrt(m.totalMC) : 0,
      sector: m.sector, subSector: m.subSector,
      avgDeltaEbit: avg(m.deltaEbits), avgMarginChg: avg(m.marginChgs), avgPePercent: avg(m.pePercents),
      avgPePrem: avg(m.pePremVals),
      avgReturn: avg(m.returns), avgDrawUpPre: avg(m.drawUpPres),
      avgDrawUpPost: avg(m.drawUpPosts),
      riskScore: rs, riskPct: rs != null ? Math.round((rs / maxRisk) * 100) : null,
      color: sectorColor(m.sector),
    };
  });
  return result;
}

function buildSectorAgg(items, ssAgg) {
  const map = {};
  items.forEach(s => {
    const k = s.sector;
    if (!map[k]) map[k] = { marginChgs: [], pePremVals: [], count: 0, totalMC: 0 };
    map[k].count++;
    if (s.marketCap != null) map[k].totalMC += s.marketCap;
    if (s.marginChg != null) map[k].marginChgs.push(s.marginChg);
    if (s.pePercent10Y != null) map[k].pePremVals.push(s.pePercent10Y - 100);
  });
  const result = {};
  Object.entries(map).forEach(([k, m]) => {
    const subs = Object.values(ssAgg).filter(s => s.sector === k);
    const riskVals = subs.map(s => s.riskPct).filter(v => v != null);
    const avgRisk = riskVals.length ? riskVals.reduce((a, b) => a + b, 0) / riskVals.length : null;
    result[k] = {
      name: k, sector: k, subSector: k, count: m.count, totalMC: Math.round(m.totalMC),
      bubbleMC: m.totalMC > 0 ? Math.sqrt(m.totalMC) : 0, subCount: subs.length,
      avgMarginChg: avg(m.marginChgs), avgPePrem: avg(m.pePremVals),
      riskPct: avgRisk != null ? Math.round(avgRisk) : null,
      color: sectorColor(k),
    };
  });
  return result;
}

