function rankByMarginChg(items, chgKey) {
  const valid = items.filter(s => s[chgKey] != null);
  if (valid.length <= 1) return valid.map(s => ({ ...s, marginRank: 50 }));
  const sorted = [...valid].sort((a, b) => a[chgKey] - b[chgKey]);
  return sorted.map((s, i) => ({ ...s, marginRank: Math.round((i / (sorted.length - 1)) * 100) }));
}

