  const [drillSector, setDrillSector] = useState(initDrill ? initDrill.sector : null);
  const [drillSub, setDrillSub] = useState(initDrill ? initDrill.subSector : null);
  const [tblSort, setTblSort] = useState({ key: "returnSince", dir: "desc" });

  const level = drillSub ? "ticker" : drillSector ? "subSector" : "sector";

  const data = useMemo(() => {
    if (level === "sector") {
      const all = Object.values(sectorAgg).filter(s => s.avgMarginChg != null && s.riskPct != null);
      return rankByMarginChg(all.map(s => ({ ...s, _level: "sector", dimmed: filter.size > 0 && !filter.has(s.sector) })), "avgMarginChg");
    }
    if (level === "subSector") {
      const all = Object.values(ssAgg).filter(s => s.sector === drillSector && s.avgMarginChg != null && s.riskPct != null);
      return rankByMarginChg(all.map(s => ({ ...s, _level: "subSector" })), "avgMarginChg");
    }
    return screen.filter(s => s.sector === drillSector && s.subSector === drillSub && s.marginChg != null && s.riskPct != null)
      .sort((a, b) => b.marginChg - a.marginChg);
  }, [level, drillSector, drillSub, filter, sectorAgg, ssAgg, screen]);

  const handleClick = (d) => {
    if (!d) return;
    if (level === "sector") {
      const subs = Object.values(ssAgg).filter(s => s.sector === d.sector);
      if (subs.length === 1) { setDrillSector(d.sector); setDrillSub(subs[0].subSector); }
      else setDrillSector(d.sector);
    }
    else if (level === "subSector") setDrillSub(d.subSector);
  };

  const resetToSectors = () => { setDrillSector(null); setDrillSub(null); };
  const resetToSubs = () => setDrillSub(null);
  const toggleSort = (key) => setTblSort(prev => ({ key, dir: prev.key === key && prev.dir === "desc" ? "asc" : "desc" }));

