function parseExpr(raw) {
  if (!raw || !raw.trim()) return null;
  const s = raw.trim();
  const m = s.match(/^([<>!=]=?)\s*(-?\d+\.?\d*)$/);
  if (m) return { op: m[1], val: parseFloat(m[2]), raw: s };
  const n = parseFloat(s);
  if (!isNaN(n)) return { op: ">=", val: n, raw: s };
  return { op: null, val: null, raw: s };
}
function evalExpr(expr, v) {
  if (!expr || expr.op == null) return true;
  if (v == null) return false;
  switch (expr.op) {
    case ">=": return v >= expr.val;
    case ">": return v > expr.val;
    case "<=": return v <= expr.val;
    case "<": return v < expr.val;
    case "=": case "==": return v === expr.val;
    case "!=": return v !== expr.val;
    default: return true;
  }
}

